mapper.select:

select 
		rno, bno, reply, replyer, replyerdate, updatedate
from
	(
		select 
				/*+INDEX(tbl_reply idx_reply) */ rownum rn, 
				rno, bno, reply, replyer, replyerDate, updatedate
		from 
				tbl_reply
		where 
				bno = #{bno}
			and rno > 0
			and rownum <= #{cri.pageNum} * #{cri.amount}
	) 
where 
		rn > (#{cri.pageNum} -1) * #{cri.amount}
