mapper.select:

select 
		rno, bno, reply, replyer, replydate, updatedate, nickname
from
	(
		select 
				/*+INDEX(reply idx_reply) */ rownum rn, 
				rno, bno, reply, replyer, replydate, updatedate, nickname
		from 
				reply
		where 
				bno = #{bno}
			and rno > 0
			and rownum <= #{cri.pageNum} * #{cri.amount}
	) 
where 
		rn > (#{cri.pageNum} -1) * #{cri.amount}
